mapper.select:
select coursePrice
from t_course
where courseId=#{courseId}

select * 
from t_orderscar
where courseId=#{courseId} and studentId=#{studentId}

mapper.update:
update t_orderscar
set count=#{count},totalPrice=#{totalPrice},time=#{time}
where studentId=#{studentId} and courseId=#{courseId}
